package j0511;

import java.util.Iterator;

public class Alpha2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a = 'A';
		for (int i = 1; i <= 5; i++) {
			for (int j = 1; j <= i; j++) {
				System.out.print(a);
				a++;
				
			}
			System.out.println();
			
		}
	}

}
